<template>
  <section class="py-20 font-roboto relative w-full mx-auto box-border">
    <div class="w-full mx-auto px-4 sm:px-6 lg:px-8 max-w-7xl box-border">
      <!-- Section Header -->
      <div class="relative pb-[61px] box-border">
        <div class="p-0 relative font-bold box-border leading-[1.1] ![margin-top:0] [font-size:14px] tracking-[-0.03em] text-[oklch(37.754%0.15587_274.67)] lg:[font-size:16px]">
          <i class="h-[2px] relative w-[30px] mr-[25px] box-border top-[-5px] inline-block [background-color:oklch(37.754%0.15587_274.67)]"></i>
          {{ sectionData.subtitle }}
        </div>
        <h2 class="mb-0 pb-0 font-bold mt-[20px] box-border -tracking-wide leading-[1.13] [word-spacing:0] [font-size:31px] text-[oklch(24.229%0.02848_284.07)] lg:[font-size:40px]">
          We Help you<br class="box-border lg:block" />
          Embrace the Future
        </h2>
      </div>
      
      <!-- Services Grid -->
      <div class="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-x-4 gap-y-16">
        <!-- Service Items using Shadcn Card -->
        <Card 
          v-for="service in services" 
          :key="service.id"
          class="group relative pl-10 border-0 shadow-none p-0 gap-0 hover:-translate-y-1 transition-transform duration-200 !flex-none"
        >
          <a :href="service.link" class="block">
            <!-- Left border line -->
            <div class="absolute left-0 top-0 w-px h-24 sm:h-20 bg-gray-200"></div>
            
            <!-- Card Content -->
            <CardContent class="p-0 ml-8">
              <!-- Icon -->
              <div class="mb-6" v-html="getServiceIcon(service.icon)"></div>
              
              <!-- Title -->
              <h4 class="text-xl font-bold tracking-tight text-gray-900 mb-4 transition-colors duration-200 group-hover:text-[#2e3192]">
                {{ service.title }}
              </h4>
            </CardContent>
          </a>
        </Card>
      </div>

      <!-- Description Sections -->
      <div class="flex flex-col lg:flex-row gap-8 w-full mt-16">
        <!-- Description Section 1 -->
        <div class="w-full lg:w-1/2">
          <div class="leading-[1.4] text-[22px] font-bold" :style="{ color: sectionData.accentColor }">
            {{ descriptions[0] }}
          </div>
        </div>

        <!-- Description Section 2 -->
        <div class="w-full lg:w-1/2">
          <div class="text-gray-600 leading-relaxed font-bold">
            {{ descriptions[1] }}
          </div>
        </div>
      </div>
    </div>
  </section>
</template>
